import { useState } from "react";

function MessageType1() {
  const [numbers, setNumbers] = useState("");
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://127.0.0.1:5000/api/numbers/1", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          numbers: numbers.split(",").map(n => n.trim()), // e.g. "1,2,3"
            iodp: parseInt(e.target[1].value), // e.g. 0
        }),
      });

      const data = await res.json();
      setResult(data.hex_string);
    } catch (err) {
      console.error("Error:", err);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">Message Type 1</h1>

      <form onSubmit={handleSubmit} className="mt-4">
        <input
          type="text"
          placeholder="Enter PRN mask for satellites (comma separated)"
          value={numbers}
          onChange={(e) => setNumbers(e.target.value)}
          className="border p-2"
        />
        <input
            type="number"
            placeholder="Enter IODP (0-3)"
            className="ml-2 border p-2"
            required
        />
        <button
          type="submit"
          className="ml-2 bg-blue-500 text-white p-2 rounded"
        >
          Submit
        </button>
      </form>

      {result && (
        <div className="mt-4 p-3 bg-gray-100 border rounded">
          <p>64-bit Hex String: {result}</p>
        </div>
      )}
    </div>
  );
}

export default MessageType1;
